INSERT INTO `jobs_position` VALUES ('76', '28', '商务专员', '五险一金,交通补助', '1、提供市场趋势、需求变化、竞争对手和客户反馈方面的准确信息</br>2、负责通过邮件、电话、见面拜访等方式积极拓展新客户，向客户推荐网络行销解决方案</br>3、维护既有客户，定期为既有客户进行自有资源的更新介绍</br>4、针对不同客户制定招商方案方案及达成合作', '6000', '0', '22', '2', '70', '10', '1、本科及以上学历；市场营销、广告学、公关等相关专业优先</br>2、具备一定资源储备，懂得公关处理；</br>3、对电子竞技行业有一定认知和兴趣</br>4、具有良好的沟通能力和业务拓展能力</br>5、具有良好的团队合作意识', '1', '3', '40', '-1', '2020-12-12 00:00:00', '1', '0', '51', '2017-10-30 11:44:48', '2017-12-15 22:00:20');
